
SELECT AVG("movies2actors"."actorid") as agg_0 FROM "u2base" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid"  WHERE "movies2directors"."genre" NOT LIKE '%Documentary%' AND "movies2actors"."cast_num" = 3 AND ("directors"."directorid" <= 35305 OR "directors"."avg_revenue" != 0) AND "u2base"."rating" BETWEEN 2 AND 4 AND "movies"."runningtime" >= 1 AND "u2base"."movieid" >= 1912336;