
SELECT AVG("directors"."directorid") as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid"  WHERE "users"."u_gender" IN ('F') AND "u2base"."movieid" >= 1757529 AND ("movies2directors"."genre" = '%Adventure%' OR ("movies2directors"."genre" LIKE '%Adventur%e%' AND "movies2directors"."genre" IN ('Animation', 'Action', 'Adventure', 'Drama', 'Other', 'Comedy', 'Crime', 'Documentary')));