
SELECT SUM("directors"."avg_revenue") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid"  WHERE ("movies2directors"."genre" NOT LIKE '%Crime%' OR "movies2directors"."genre" != '%Other%') AND "u2base"."userid" <= 2936 AND "users"."u_gender" IN ('M') AND ("movies"."runningtime" BETWEEN 1 AND 3 OR ("movies"."runningtime" <= 2 AND "movies"."runningtime" BETWEEN 1 AND 3)) AND "movies2actors"."cast_num" <= 3;