
SELECT MAX("u2base"."movieid") as agg_0 FROM "movies" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE ("movies"."country" = '%USA%' OR "movies"."isEnglish" IN ('T')) AND "movies2directors"."genre" IN ('Horror', 'Animation', 'Other') AND "actors"."a_gender" != '%M%' AND "users"."occupation" <= 4;