
SELECT MIN("movies2actors"."cast_num") as agg_0 FROM "movies" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE "movies2directors"."directorid" <= 60318 AND "movies2actors"."movieid" <= 2332123 AND "movies"."country" LIKE '%France%' AND "actors"."a_quality" BETWEEN 0 AND 4 AND "u2base"."userid" >= 5231 AND "movies"."year" = 4 AND "directors"."directorid" IS NOT NULL;