
SELECT MIN("users"."age") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid"  WHERE "movies"."country" != '%France%' AND "users"."u_gender" = '%F%' AND "movies2directors"."genre" NOT LIKE '%Crime%' AND "u2base"."rating" <= 3 AND "movies2directors"."genre" IN ('Drama', 'Action', 'Documentary', 'Adventure', 'Crime', 'Horror', 'Other') AND "directors"."directorid" IS NOT NULL;