
SELECT MIN("u2base"."movieid") as agg_0 FROM "actors" LEFT OUTER JOIN "movies2actors" ON "actors"."actorid" = "movies2actors"."actorid" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid"  WHERE "movies"."country" = '%France%' AND "movies"."country" IN ('UK', 'other', 'USA') AND "actors"."actorid" <= 1753632 AND "users"."userid" IS NOT NULL;