
SELECT MIN("directors"."d_quality") as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE ("movies"."country" LIKE '%UK%' OR ("movies"."runningtime" BETWEEN 1 AND 2 AND "movies"."country" LIKE '%U%SA%')) AND ("directors"."d_quality" != 3 OR "directors"."avg_revenue" <= 1) AND "movies"."year" != 4 AND "movies"."isEnglish" IN ('T') AND "u2base"."movieid" IS NOT NULL;