
SELECT AVG("movies"."runningtime") as agg_0 FROM "actors" LEFT OUTER JOIN "movies2actors" ON "actors"."actorid" = "movies2actors"."actorid" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE ("movies2directors"."genre" LIKE '%Drama%' OR "movies2directors"."movieid" >= 2526578) AND "actors"."actorid" <= 2386156 AND ("movies"."isEnglish" IN ('F') OR ("movies"."movieid" <= 2487874 AND "movies"."isEnglish" != '%F%')) AND "u2base"."movieid" IS NOT NULL;