
SELECT AVG("directors"."directorid") as agg_0 FROM "movies2directors" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid"  WHERE "movies"."year" BETWEEN 2 AND 4 AND "movies2actors"."movieid" BETWEEN 1843869 AND 2310140 AND "u2base"."movieid" <= 2428082 AND "movies2directors"."genre" LIKE '%C%rime%' AND "movies"."country" = '%other%';