
SELECT MIN("users"."occupation") as agg_0 FROM "actors" LEFT OUTER JOIN "movies2actors" ON "actors"."actorid" = "movies2actors"."actorid" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE "actors"."actorid" >= 2177103 AND "movies2directors"."movieid" <= 2445606 AND "u2base"."userid" BETWEEN 1066 AND 3280 AND "movies2directors"."genre" LIKE '%H%orror%' AND "users"."u_gender" != '%M%';