
SELECT AVG("users"."occupation") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid"  WHERE "movies"."isEnglish" IN ('F') AND "users"."userid" >= 2551 AND "u2base"."movieid" <= 1691740 AND "movies"."runningtime" <= 3;