
SELECT AVG("movies2directors"."directorid") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid"  WHERE "users"."u_gender" IN ('M') AND "movies"."country" IN ('other') AND "movies2actors"."cast_num" BETWEEN 1 AND 3 AND "movies"."country" NOT LIKE '%U%K%' AND "users"."age" <= 33 AND "movies2directors"."genre" IN ('Documentary', 'Adventure', 'Horror', 'Crime');