
SELECT AVG("users"."userid") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid"  WHERE "movies"."runningtime" BETWEEN 2 AND 3 AND ("users"."occupation" = 2 OR "users"."age" BETWEEN 35 AND 45) AND "movies"."country" != '%UK%';