
SELECT MAX("users"."occupation") as agg_0 FROM "u2base" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid"  WHERE "movies2directors"."movieid" >= 2461936 AND "movies2directors"."genre" = '%Horror%' AND "movies"."country" LIKE '%Fran%ce%' AND "movies2directors"."genre" IN ('Horror', 'Action', 'Animation', 'Crime', 'Comedy', 'Other', 'Drama', 'Documentary') AND "movies2actors"."actorid" >= 1921852;