
SELECT SUM("u2base"."userid") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE ("movies2actors"."cast_num" BETWEEN 2 AND 3 OR ("movies2actors"."cast_num" <= 1 AND "movies2actors"."cast_num" >= 1)) AND ("movies2directors"."genre" LIKE '%Animation%' OR "movies2directors"."movieid" <= 2155845 OR "movies2directors"."genre" LIKE '%Horror%') AND "movies"."year" <= 3 AND "movies2directors"."directorid" <= 16824;