
SELECT SUM("directors"."directorid") as agg_0 FROM "movies2directors" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE "movies2actors"."cast_num" BETWEEN 0 AND 2 AND "movies2directors"."genre" LIKE '%Comedy%' AND "u2base"."userid" BETWEEN 291 AND 4024 AND "movies2directors"."directorid" BETWEEN 190787 AND 267777;