
SELECT AVG("actors"."a_quality") as agg_0 FROM "movies2actors" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE "movies2actors"."actorid" <= 208454 AND "movies"."isEnglish" IN ('T') AND "actors"."a_quality" != 4 AND "movies"."year" BETWEEN 4 AND 5 AND "movies2directors"."movieid" IS NOT NULL AND "u2base"."movieid" IS NOT NULL;