
SELECT COUNT(*) as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid"  WHERE "movies"."country" IN ('USA', 'UK') AND ("movies2directors"."genre" = '%Drama%' OR "movies2directors"."genre" LIKE '%Animation%' OR "movies2directors"."genre" NOT LIKE '%Cri%me%') AND "movies2actors"."actorid" BETWEEN 130351 AND 1313260 AND "actors"."a_gender" != '%F%' AND "movies2directors"."directorid" BETWEEN 42059 AND 51315;