
SELECT SUM("movies2actors"."cast_num") as agg_0 FROM "movies" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE "movies"."runningtime" <= 3 AND ("movies2directors"."genre" NOT LIKE '%Dr%ama%' OR "movies2directors"."genre" IN ('Comedy', 'Horror', 'Adventure')) AND ("actors"."a_quality" BETWEEN 3 AND 4 OR ("actors"."a_gender" != '%M%' AND "actors"."a_quality" BETWEEN 3 AND 4)) AND ("movies2directors"."genre" LIKE '%Animation%' OR ("movies2directors"."movieid" BETWEEN 2397597 AND 2446959 AND "movies2directors"."genre" != '%Documentary%')) AND "movies2actors"."cast_num" <= 3 AND "u2base"."movieid" IS NOT NULL;