
SELECT AVG("movies2actors"."movieid") as agg_0 FROM "movies" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE ("movies2directors"."directorid" BETWEEN 106744 AND 159194 OR "movies2directors"."movieid" <= 1832637) AND "movies2actors"."actorid" BETWEEN 468397 AND 2679616 AND "actors"."a_quality" BETWEEN 3 AND 4 AND "movies2actors"."cast_num" <= 2 AND "movies2directors"."genre" IN ('Adventure', 'Crime') AND "movies"."year" <= 4 AND "u2base"."movieid" IS NOT NULL;