
SELECT AVG("movies"."movieid") as agg_0 FROM "movies2directors" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE "movies"."isEnglish" IN ('T') AND "movies2actors"."movieid" >= 1683170 AND "movies"."runningtime" >= 3 AND ("u2base"."userid" <= 5563 OR ("u2base"."userid" >= 697 AND "u2base"."userid" <= 788)) AND "u2base"."movieid" BETWEEN 1698136 AND 2568457 AND "movies"."country" NOT LIKE '%U%SA%';