
SELECT MAX("movies"."year") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE "movies2directors"."movieid" <= 2238700 AND "movies2directors"."genre" NOT LIKE '%Documentary%' AND "users"."occupation" BETWEEN 2 AND 4 AND "movies"."country" NOT LIKE '%other%' AND "movies2actors"."movieid" IS NOT NULL;