SELECT COUNT(*) as agg_0, MAX("probehnuto"."client_id" + "probehnuto"."kc_proklikano") as agg_1, SUM("probehnuto_mimo_penezenku"."client_id" + "client"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto"."client_id" IS NOT NULL AND "probehnuto"."month_year_datum_transakce" NOT LIKE '%2015-%09-01%' AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%ANO%'