SELECT COUNT(*) as agg_0, AVG("probehnuto_mimo_penezenku"."client_id") as agg_1, AVG("probehnuto_mimo_penezenku"."client_id" + "client"."client_id") as agg_2 FROM "client" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."kraj" IN ('Plzeňský kraj', 'Moravskoslezský kraj', 'Praha', 'Olomoucký kraj', 'Jihomoravský kraj', 'Jihočeský kraj', 'Ústecký kraj', 'Zlínský kraj', 'Středočeský kraj') AND "client"."client_id" <= 9787172 AND "probehnuto_mimo_penezenku"."client_id" BETWEEN 95582 AND 564933 AND "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2014-%02-01%' AND "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2014-0%2-01%'