SELECT COUNT(*) as agg_0, SUM("dobito"."kc_dobito") as agg_1, MAX("dobito"."client_id") as agg_2 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "dobito"."month_year_datum_transakce" != '2015-02-01' OR "dobito"."month_year_datum_transakce" NOT LIKE '%2013-09-01%' OR "dobito"."month_year_datum_transakce" LIKE '%2015-%09-01%'