SELECT SUM("probehnuto_mimo_penezenku"."client_id" + "probehnuto"."kc_proklikano") as agg_0, COUNT(*) as agg_1, SUM("dobito"."kc_dobito" + "dobito"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2014-02-0%1%' AND "client"."kraj" IS NULL AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%A%NO%' AND ("dobito"."month_year_datum_transakce" LIKE '%2014-03%-01%' OR "dobito"."sluzba" = 'b' OR "dobito"."month_year_datum_transakce" IN ('2014-10-01', '2014-12-01', '2015-10-01', '2014-11-01', '2015-04-01', '2015-09-01', '2015-06-01', '2015-05-01', '2014-03-01')) AND "probehnuto_mimo_penezenku"."client_id" <= 9464976