SELECT AVG("dobito"."client_id") as agg_0, MIN("probehnuto"."kc_proklikano" + "client"."client_id") as agg_1 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "dobito"."sluzba" IN ('a', 'b', 'd', 'h', 'e', 'c') AND "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-05-01%'