SELECT MIN("client"."client_id") as agg_0, MIN("dobito"."client_id" + "client"."client_id") as agg_1 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."kraj" IN ('Plzeňský kraj', 'Středočeský kraj', 'Jihočeský kraj', 'Moravskoslezský kraj', 'Zlínský kraj', 'Ústecký kraj', 'Královéhradecký kraj', 'Jihomoravský kraj') AND ("client"."kraj" = 'Plzeňský kraj' OR "client"."client_id" <= 11524 OR "client"."kraj" LIKE '%Prah%a%') AND ("probehnuto_mimo_penezenku"."client_id" <= 9077712 OR "probehnuto_mimo_penezenku"."client_id" >= 61856 OR "probehnuto_mimo_penezenku"."client_id" <= 899844) AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%ANO%'