SELECT MAX("probehnuto"."client_id" + "client"."client_id") as agg_0, SUM("probehnuto"."client_id" + "probehnuto_mimo_penezenku"."client_id") as agg_1 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto"."sluzba" = 'e' AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" != 'ANO'