SELECT MAX("dobito"."client_id") as agg_0, COUNT(*) as agg_1, MIN("probehnuto"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "client"."kraj" NOT LIKE '%Jihomoravský%' AND "client"."obor" != 'Erika' AND "probehnuto"."kc_proklikano" BETWEEN 353.6543172644658 AND 16108317.66635257 AND "client"."kraj" NOT LIKE '%Královéh%radecký%' AND "dobito"."client_id" IS NOT NULL