SELECT MAX("client"."client_id") as agg_0, MAX("client"."client_id" + "probehnuto"."kc_proklikano") as agg_1 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "client"."obor" IN ('Richard', 'Ozzy', 'Floyd', 'Vladan') AND "client"."kraj" = 'Královéhradecký kraj'