SELECT COUNT(*) as agg_0, MIN("probehnuto"."client_id" + "dobito"."client_id") as agg_1 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "dobito"."month_year_datum_transakce" IN ('2015-03-01', '2015-06-01') AND "probehnuto"."month_year_datum_transakce" IN ('2015-04-01', '2014-06-01', '2014-05-01', '2014-11-01', '2014-10-01', '2014-07-01', '2014-08-01') AND "client"."kraj" NOT LIKE '%Praha%' AND "dobito"."sluzba" IN ('e', 'b')