SELECT MIN("probehnuto_mimo_penezenku"."client_id") as agg_0, AVG("probehnuto"."client_id" + "client"."client_id") as agg_1 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "dobito"."month_year_datum_transakce" = '2015-08-01' AND "probehnuto_mimo_penezenku"."Month/Year" IN ('2013-01-01', '2012-09-01', '2012-11-01') AND "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-03-01%'