SELECT SUM("client"."client_id") as agg_0, COUNT(*) as agg_1, MIN("dobito"."client_id" + "probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "dobito"."month_year_datum_transakce" != '2014-06-01'