SELECT COUNT(*) as agg_0, AVG("dobito"."kc_dobito" + "probehnuto"."client_id") as agg_1 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE ("client"."kraj" LIKE '%Jihočes%ký%' OR "client"."obor" NOT LIKE '%Hermi%na%') AND "dobito"."sluzba" = 'g' AND ("probehnuto_mimo_penezenku"."Month/Year" LIKE '%20%12-09-01%' OR "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" = 'ANO' OR "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2012-09-%01%')