SELECT COUNT(*) as agg_0 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE ("probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-01-0%1%' OR "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-09-01%' OR "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2012-1%1-01%') AND "client"."kraj" LIKE '%Zlín%ský%'