SELECT MAX("client"."client_id") as agg_0, COUNT(*) as agg_1 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."client_id" BETWEEN 635357 AND 9729934 AND "client"."obor" IN ('Gabriel', 'Floyd', 'Richard')