SELECT AVG("client"."client_id") as agg_0, COUNT(*) as agg_1, SUM("probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id"  WHERE "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2013-02-01%' AND "client"."kraj" LIKE '%Středočeský%' AND "probehnuto_mimo_penezenku"."Month/Year" IN ('2012-12-01', '2013-01-01', '2013-06-01', '2012-10-01', '2013-05-01', '2013-04-01', '2013-02-01', '2013-03-01', '2012-11-01') AND "probehnuto_mimo_penezenku"."client_id" <= 9236343 AND "client"."kraj" IS NOT NULL AND "client"."client_id" >= 999144