SELECT AVG("dobito"."kc_dobito") as agg_0, SUM("probehnuto"."client_id") as agg_1, MIN("probehnuto"."kc_proklikano" + "probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto"."month_year_datum_transakce" LIKE '%2015-07-01%' AND "dobito"."client_id" <= 4586586.852142272 AND "probehnuto"."client_id" >= 103958.84794519651 AND "dobito"."sluzba" IN ('d', 'h', 'f', 'b', 'e', 'a') AND ("probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%AN%O%' OR "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" = 'ANO' OR "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%ANO%') AND "dobito"."kc_dobito" >= 13333.873376062573