SELECT SUM("client"."client_id" + "probehnuto"."client_id") as agg_0, SUM("probehnuto"."kc_proklikano" + "probehnuto"."client_id") as agg_1 FROM "client" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto"."client_id" <= 31012.361751422955 AND "client"."kraj" IN ('Jihomoravský kraj', 'Středočeský kraj', 'Praha', 'Ústecký kraj', 'Jihočeský kraj', 'Moravskoslezský kraj') AND "probehnuto"."sluzba" != 'e' AND "probehnuto"."month_year_datum_transakce" != '2013-08-01'