SELECT COUNT(*) as agg_0, SUM("dobito"."client_id" + "probehnuto"."client_id") as agg_1, SUM("probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."kraj" IN ('Královéhradecký kraj', 'Zlínský kraj', 'Jihočeský kraj', 'Středočeský kraj', 'Jihomoravský kraj', 'Praha', 'Olomoucký kraj', 'Plzeňský kraj') AND "client"."obor" IN ('Zora', 'Erika', 'Ozzy', 'Anezka', 'Vladan')