SELECT SUM("probehnuto_mimo_penezenku"."client_id") as agg_0, MAX("client"."client_id") as agg_1 FROM "client" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "client"."obor" NOT LIKE '%Josef%' AND "probehnuto"."sluzba" != 'h' AND "probehnuto"."client_id" BETWEEN 58926.718913463235 AND 1645109.9013290654 AND "dobito"."month_year_datum_transakce" LIKE '%2014-06-01%' AND "probehnuto_mimo_penezenku"."Month/Year" != '2015-02-01'