SELECT AVG("probehnuto"."client_id" + "client"."client_id") as agg_0, MIN("dobito"."kc_dobito") as agg_1 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "probehnuto"."sluzba" IN ('e', 'c', 'a', 'd') AND ("dobito"."month_year_datum_transakce" LIKE '%2015-%10-01%' OR "dobito"."sluzba" != 'c' OR "dobito"."month_year_datum_transakce" NOT LIKE '%2015-08-01%') AND "client"."kraj" LIKE '%Plzeňský%'