SELECT MAX("client"."client_id") as agg_0, MAX("client"."client_id" + "probehnuto_mimo_penezenku"."client_id") as agg_1, SUM("client"."client_id") as agg_2 FROM "client" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto_mimo_penezenku"."Month/Year" IN ('2013-02-01', '2012-09-01', '2013-05-01', '2013-01-01', '2013-03-01', '2012-11-01', '2012-10-01', '2013-06-01', '2013-04-01') AND "client"."kraj" LIKE '%M%oravskoslezský%' AND "client"."obor" IN ('Pink', 'Gabriel', 'Ozzy', 'Anezka', 'Vladan') AND "client"."kraj" LIKE '%Olomouc%ký%' AND "probehnuto_mimo_penezenku"."Month/Year" != '2012-12-01'