SELECT AVG("probehnuto_mimo_penezenku"."client_id" + "probehnuto"."client_id") as agg_0, MAX("dobito"."client_id") as agg_1, MIN("dobito"."client_id" + "probehnuto"."kc_proklikano") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto_mimo_penezenku"."client_id" >= 4254 OR "probehnuto_mimo_penezenku"."client_id" BETWEEN 51278 AND 4760787