SELECT AVG("client"."client_id") as agg_0, COUNT(*) as agg_1, AVG("probehnuto"."kc_proklikano") as agg_2 FROM "client" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "dobito"."month_year_datum_transakce" IN ('2015-06-01', '2014-03-01', '2014-10-01', '2015-04-01', '2015-03-01', '2014-11-01', '2015-10-01', '2014-12-01') AND ("probehnuto"."month_year_datum_transakce" = '2013-08-01' OR "probehnuto"."month_year_datum_transakce" LIKE '%2015-07-01%' OR "probehnuto"."month_year_datum_transakce" NOT LIKE '%2015-01-01%') AND "client"."obor" LIKE '%Alice%'