SELECT MIN("client"."client_id") as agg_0, MIN("probehnuto"."kc_proklikano") as agg_1, MIN("client"."client_id" + "probehnuto"."kc_proklikano") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "dobito"."month_year_datum_transakce" LIKE '%2015-09-01%'