SELECT MIN("probehnuto_mimo_penezenku"."client_id") as agg_0 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "client"."obor" IN ('Anezka', 'Ozzy', 'Hermina', 'Richard', 'Floyd', 'Vladan', 'Gabriel') AND "probehnuto"."month_year_datum_transakce" LIKE '%2015%-10-01%'