SELECT SUM("probehnuto_mimo_penezenku"."client_id") as agg_0, AVG("probehnuto"."kc_proklikano" + "probehnuto_mimo_penezenku"."client_id") as agg_1, MIN("probehnuto"."kc_proklikano" + "dobito"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto_mimo_penezenku"."Month/Year" != '2012-11-01' AND "dobito"."sluzba" = 'd' AND "probehnuto"."month_year_datum_transakce" = '2012-12-01' AND "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-08-01%' AND "probehnuto"."sluzba" IN ('c', 'g')